const jsonString = $input.first().json.output;

// Extract title and caption using regex
const titleMatch = jsonString.match(/"title":\s*"([^"]+)"/);
const captionMatch = jsonString.match(/"caption":\s*"([^"]+)"/);

// Get values or set to empty string if not found
const title = titleMatch ? titleMatch[1] : "";
const caption = captionMatch ? captionMatch[1] : "";

return [{ 
  json: { 
    title, 
    caption 
  } 
}];

